import { Component,OnInit } from '@angular/core';
import { ProductService } from '../api/product.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css']
})
export class ItemComponent implements OnInit{
  routeId:any;
  product:any
  constructor(public items:ProductService,public active:ActivatedRoute){

  }

  ngOnInit(): void {
    this.routeId=this.active.snapshot.paramMap.get("id")
    console.log(this.routeId)
    this.product=this.items.getProduct().find(a=>a.id==this.routeId)
    console.log(this.product)
  }

}
<app-header></app-header>
<div class="container">
    <img src="../assets/images/products/{{product.img}}" width="100%" height="300px">
    <div class="text-white bg-warning p-4 mt-3">
        <h1 class="text-danger">{{product.name}}</h1>
       <p>{{product}}</p> 
    </div>
    <div class="text-center mt-4">
        <button class="btn btn-success me-2">Price:5000/-</button>
        <button class="btn btn-danger">Add To Cart</button>
    </div>
</div>
<app-footer></app-footer>
