import { Component } from '@angular/core';

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  public  products=[
    {
      name:"VenkateswaraSwamy",
      img:"p1.jpg",
      content:"Sri Venkateswara Swami Vaari Temple is a Hindu temple situated in the hill town of Tirumala at Tirupati in Tirupati district of Andhra Pradesh, India. The Temple is dedicated to Venkateswara, a form of Vishnu, who is believed to have appeared on the earth to save mankind from trials and troubles of Kali Yuga.",
      price:5000,
      cart:"AddToCart",
      duration:"Readmore"
    },
    {
        name:"Saibaba",
        img:"p2.jpg",
        content:"It is dedicated to the Indian saint Sai Baba of Shirdi. The temple was built in 1952 by one B V Narasimhaswami, a Salem and Sai Baba devotee, out of money donated by a Chettiar merchant. This is considered the most trusted temple in India. The temple is the headquarters of the All India Sai Samaj.",
        price:5000,
        cart:"AddToCart",
        duration:"Readmore"   
    },
    {
        name:"AnanthaPadmanabhaSwamy",
        img:"p3.jpg",
        content:"The Sree Padmanabhaswamy Temple is renowned for its beauty and grandeur. Dedicated to LordVishnu, it is one of the 108 sacred Vishnu temples or 'Divya Deshams' in India. The great King of Travancore, Marthanda Varma is said to have done the renovation work that we see today.",
        price:5000,
        cart:"AddToCart",
        duration:"Readmore"   
    },
    {
        name:"Badrinath Temple",
        img:"p5.jfif",
        content:"Dedicated to Lord Vishnu, the Badrinath Temple is one of the most important pilgrimage sites in India. It is among the four holy places or char dham, which are much revered by Hindus. Devotees undertake a strenuous journey through the mighty Himalayas to reach this holy shrine.",
        price:5000,
        cart:"AddToCart",
        duration:"Readmore"   
    },
    {
        name:"kashivishwanath temple.",
        img:"p6.jpg",
        content:"The temple has been destroyed by invading forces and rebuilt three times. The current temple is built directly next to a mosque which is built on the ruins of an earlier version of Vishwanath. Both sides have filed charges seeking to determine who has the right to the property in the area.",
        price:5000,
        cart:"AddToCart",
        duration:"Readmore"   
    },
    {
        name:"KanakaDurgamma",
        img:"p4.jpg",
        content:"As per his wishes of killing the demons, Goddess Durga made Indrakila Her permanent abode. Later, She also slayed the demon king Mahishasura freeing the people of Vijayawada from evil. At the Kanakadurga temple, the enchanting 4-foot-high (1.2 m) icon of the deity is bedecked in glittering ornaments and bright flowers.",
        price:5000,
        cart:"AddToCart",
        duration:"Readmore"   
    },

  ]
}
